SELECT "WP-SINDICATOS".CODIGO,
"WP-SINDICATOS".CATEGORIA,
"WP-SINDICATOS".NOME,
"WP-SINDICATOS".ENDERECO,
"WP-SINDICATOS".BAIRRO,
"G-MUNICIPIOS".DESCRICAO AS CIDADE,
"G-MUNICIPIOS".UF AS ESTADO,
"WP-SINDICATOS".CEP,
"WP-SINDICATOS".CNPJ,
"WP-SINDICATOS".TELEFONE,
"WP-SINDICATOS".FAX,
"WP-SINDICATOS".MES_DISSIDIO,
"WP-SINDICATOS".E_MAIL,
"WP-SINDICATOS".CODIGO_MTB,
"WP-SINDICATOS".PISO_SALARIAL
FROM "WP-SINDICATOS" LEFT JOIN "G-MUNICIPIOS"
ON "G-MUNICIPIOS".MUNICIPIOS_ID = "WP-SINDICATOS".MUNICIPIOS_ID
ORDER BY "WP-SINDICATOS".SINDICATOS_ID