 SELECT CODIGO_EXTERNO,
		NOME,
		CNPJ,
		CPF,
		CEI,
		TIPO,
		TIPO_LOGRADOURO,
		ENDERECO,
		ENDERECO_NUMERO,
		ENDERECO_COMPLEMENTO,
		CEP,
		BAIRRO,		
		TELEFONE_DDD,
		TELEFONE,
		TELEFONE_CELULAR,
		(SELECT "G-MUNICIPIOS".CODIGO_IBGE FROM "G-MUNICIPIOS" WHERE "G-CONTABILISTAS".MUNICIPIOS_ID = "G-MUNICIPIOS".MUNICIPIOS_ID) AS CODIGO_IBGE,
		(SELECT "G-MUNICIPIOS".DESCRICAO FROM "G-MUNICIPIOS" WHERE "G-CONTABILISTAS".MUNICIPIOS_ID = "G-MUNICIPIOS".MUNICIPIOS_ID) AS CIDADE,
		(SELECT "G-MUNICIPIOS".UF FROM "G-MUNICIPIOS" WHERE "G-CONTABILISTAS".MUNICIPIOS_ID = "G-MUNICIPIOS".MUNICIPIOS_ID) AS ESTADO,
		E_MAIL,
		CARGO,
		NACIONALIDADE,
		ESTADO_CIVIL,
		RG_NUMERO,
		RG_ORGAO_EXP,
		RG_UF,
		DATA_NASCIMENTO,
		DATA_INICIO,
		DATA_FIM,
		SOCIO,
		CAPITAL_PERCENTUAL,
		FUNCAO,
		QUALIFICACAO,		
		QUALIFICACAO_SOCIETARIA,
		PAIS_ID,
		EMPRESA_SOCIO,
		EMPRESA_SN,
		EMPRESA_ESCRITORIO_ID,
		FILIAL_ID_SOCIO,
		(SELECT "G-EMPRESAS".CODIGO_EXTERNO FROM "G-EMPRESAS" WHERE "G-CONTABILISTAS".EMPRESA_SOCIO = "G-EMPRESAS".CODIGO) AS EMPRESA,
		(SELECT MIN("G-EMPRESAS_FILIAIS".CAPITAL_SOCIAL) FROM "G-EMPRESAS_FILIAIS" WHERE "G-EMPRESAS_FILIAIS".EMPRESA_ID = "G-CONTABILISTAS".EMPRESA_SOCIO ) as CAPITAL_SOCIAL,
		(SELECT MIN("G-EMPRESAS_FILIAIS".CAPITAL_SOCIAL) FROM "G-EMPRESAS_FILIAIS" WHERE "G-EMPRESAS_FILIAIS".EMPRESA_ID = "G-CONTABILISTAS".EMPRESA_SOCIO ) * (CAPITAL_PERCENTUAL/100) AS CAPITAL_INDIVIDUAL
   FROM "G-CONTABILISTAS"
  WHERE "G-CONTABILISTAS".TIPO != '1'
ORDER BY 1