SELECT EMP.CODIGO_EXTERNO AS EMPRESA,
       WP_LOTACOES_TRIBUTARIAS.LOTACOES_TRIBUTARIAS_ID,
       WP_LOTACOES_TRIBUTARIAS.DATA_INICIAL,
       TOM.NOME,
       TOM.CNPJ,
       TOM.TELEFONE,
       TOM.INSC_ESTADUAL,
       TOM.INSC_MUNICIPAL,
       TOM.EMAIL,
       ENDE.LOGRADOURO,
       ENDE.BAIRRO,
       ENDE.NUMERO,
       ENDE.COMPLEMENTO,
       ENDE.CEP,
       MUN.DESCRICAO AS MUNICIPIO,
       ENDE.UF,
       MUN.CODIGO_IBGE,
       FPAS.COD_FPAS,
       FPAS.INSS_PERC_EMPRESA,
       FPAS.TERCEIROS_CODIGO,
       FPAS.TERCEIROS_PERC,
       FPAS.INSS_PERC_EMPREGADORES_AUT,
       EMP.CNAEFISCAL,
       WP_LOTACOES_TRIBUTARIAS.TIPO_LOCAL_LOTACAO,
       WP_LOTACOES_TRIBUTARIAS.TIPO_LOTACAO,
       WP_LOTACOES_TRIBUTARIAS.ESOCIAL_CODIGO_ANTIGO
  FROM WP_LOTACOES_TRIBUTARIAS
  LEFT JOIN "G-EMPRESAS" EMP
    ON EMP.CODIGO = WP_LOTACOES_TRIBUTARIAS.EMPRESA_ID
  LEFT JOIN "WP-FPAS" FPAS
    ON FPAS.FPAS_ID = WP_LOTACOES_TRIBUTARIAS.FPAS_ID
  LEFT JOIN "G-CLI_FOR" TOM
    ON TOM."ID" = WP_LOTACOES_TRIBUTARIAS.TOMADORES_ID
  LEFT JOIN G_ENDERECOS ENDE
    ON ENDE.ENDERECOS_ID = TOM.ENDERECO_ID
  LEFT JOIN "G-MUNICIPIOS" MUN
    ON MUN.MUNICIPIOS_ID = ENDE.MUNICIPIOS_ID
 WHERE WP_LOTACOES_TRIBUTARIAS.TIPO_LOCAL_LOTACAO = 'T'
   AND WP_LOTACOES_TRIBUTARIAS.TOMADORES_ID IS NOT NULL
 ORDER BY 1, 2;