SELECT 
(SELECT "G-EMPRESAS".CODIGO_EXTERNO 
   FROM "G-EMPRESAS" 
  WHERE "WP-PESSOAL_TEMPO".EMPRESA_ID = "G-EMPRESAS".CODIGO) AS EMPRESA,
(SELECT "WP-PESSOAL".CODIGO
   FROM "WP-PESSOAL", "WP-PESSOAL_CONTRATOS"
  WHERE "WP-PESSOAL_CONTRATOS".EMPRESA_ID = "WP-PESSOAL".EMPRESA_ID
    AND "WP-PESSOAL_CONTRATOS".PESSOAL_ID = "WP-PESSOAL".PESSOAL_ID
    AND "WP-PESSOAL_TEMPO".EMPRESA_ID = "WP-PESSOAL_CONTRATOS".EMPRESA_ID
    AND "WP-PESSOAL_TEMPO".PESSOAL_CONTRATOS_ID = "WP-PESSOAL_CONTRATOS".PESSOAL_CONTRATOS_ID ) AS EMPREGADO,
"WP-PESSOAL_TEMPO".DATA_TEMPO,
"WP-PESSOAL_TEMPO".SALARIO,
"WP-PESSOAL_TEMPO".MOTIVO_ALTERACAO,
"WP-PESSOAL_TEMPO".TIPO_FOLHA,
"WP-PESSOAL_TEMPO".FORMA_PGTO,
"WP-PESSOAL_TEMPO".TIPO_SALARIO
FROM "WP-PESSOAL_TEMPO"
ORDER BY 1,2,3