SELECT A1.EMPRESA,
	   A1.MATRICULA,
	   A1.MOTIVO,
	   A1.DATA_AFASTAMENTO,
	   A1.DATA_RETORNO -1 AS DATA_RETORNO,
	   A2.CID,
	   (DATA_RETORNO - DATA_AFASTAMENTO) AS DIAS
	FROM P_TRABALHADORES_AFASTAMENTOS AS A1 LEFT JOIN P_TRABALHADORES_AFASTAMENTOS_A AS A2
									    ON A1.EMPRESA = A2.EMPRESA 
									   AND A1.MATRICULA = A2.MATRICULA 
									   AND A1.SEQUENCIAL = A2.SEQUENCIAL 
WHERE NOT A1.DATA_AFASTAMENTO IS NULL
  AND A1.MOTIVO NOT IN ('15')
ORDER BY 1,2,4