SELECT P_TRABALHADORES_REAJUSTES.*,
CASE WHEN P_TRABALHADORES_REAJUSTES.TIPO_REAJUSTE = 1 THEN ( ( P_TRABALHADORES_REAJUSTES.SALARIO_ANTERIOR * P_TRABALHADORES_REAJUSTES.REAJUSTE / 100 ) + P_TRABALHADORES_REAJUSTES.SALARIO_ANTERIOR )
            ELSE ( P_TRABALHADORES_REAJUSTES.SALARIO_ANTERIOR + P_TRABALHADORES_REAJUSTES.REAJUSTE )
       END as NOVO_SALARIO FROM P_TRABALHADORES_REAJUSTES ORDER BY 1,2