SELECT EMPRESA,
C_PLANO_CONTAS.CONTA,
C_PLANO_CONTAS.DESCRICAO,
C_PLANO_CONTAS.SEQUENCIA,
C_PLANO_CONTAS.TIPO,
C_PLANO_CONTAS.NATUREZA
FROM EMPRESAS_EXERCICIOS INNER JOIN C_PLANO_CONTAS
							     ON C_PLANO_CONTAS.TIPO_PLANO_CONTAS = EMPRESAS_EXERCICIOS.TIPO_PLANO_CONTAS 
WHERE EMPRESAS_EXERCICIOS.EXERCICIO = (SELECT FIRST 1 SUB.EXERCICIO
										FROM EMPRESAS_EXERCICIOS AS SUB
									   WHERE SUB.EMPRESA = EMPRESAS_EXERCICIOS.EMPRESA
									   ORDER BY SUB.EXERCICIO DESC)
ORDER BY 1, 2