SELECT DISTINCT SINDICATO,
P_RUBRICAS.RUBRICA,
P_RUBRICAS.TIPO_RUBRICA,
P_RUBRICAS.TIPO,
P_RUBRICAS.DESCRICAO,
P_RUBRICAS.CLASSIFICACAO,
P_RUBRICAS.FATOR
FROM P_SINDICATOS_MEDIAS_RUBRICAS INNER JOIN P_RUBRICAS
ON P_SINDICATOS_MEDIAS_RUBRICAS.RUBRICA = P_RUBRICAS.RUBRICA
AND P_SINDICATOS_MEDIAS_RUBRICAS.TIPO_RUBRICA = P_RUBRICAS.TIPO_RUBRICA
ORDER BY 2,1,3