SELECT P_TRABALHADORES.EMPRESA,
	   lpad(P_TRABALHADORES.MATRICULA, 6, '0')MATRICULA,
	   MATRICULA_ESOCIAL,
	   TIPO_CARGO,
	   CARGO,
	   NOME,
	   NASCIMENTO_DATA,
	   SEXO,
	   ESTADO_CIVIL,
	   RACA_COR,
	   PAI,
	   MAE,
	   CTPS,
	   CTPS_SERIE,
	   CTPS_UF,
	   CTPS_DATA_EXPEDICAO,
	   NIS,
	   TITULO_ELEITOR,
	   TITULO_ELEITOR_ZONA,
	   TITULO_ELEITOR_SECAO,
	   RESERVISTA,
	   RESERVISTA_SERIE,
	   HORAS_SEMANAIS,
	   HORAS_MENSAIS,
	   BANCO,
	   BANCO_AGENCIA,
	   CIDADE,
	   UF,
	   BANCO_CONTA,
	   TIPO_ENDERECO,
	   ENDERECO,
	   NUMERO,
	   COMPLEMENTO,
	   CEP,
	   BAIRRO,
	   MUNICIPIO,
	   NASCIMENTO_MUNICIPIO,
	   NASCIMENTO_NATURALIDADE,
	   NASCIMENTO_UF,
	   IDENTIFICACAO_TIPO,
	   IDENTIFICACAO_NUMERO,
	   IDENTIFICACAO_DATA_EXPEDICAO,
	   IDENTIFICACAO_ORGAO,
	   CNH_NUMERO,
	   CNH_ORGAO,
	   CNH_DATA_EXPEDICAO,
	   CNH_VALIDADE,
	   CNH_CATEGORIA,
	   EMAIL,
	   TELEFONE,
	   SINDICATO,
	   CPF,
	   ADMISSAO_DATA,
	   CATEGORIA,
	   SALARIO_FIXO,
	   SALARIO_FIXO_UNIDADE,
	   NASCIMENTO_PAIS,
	   ESTRANGEIRO_DATA_CHEGADA,
	   ESTRANGEIRO_DATA_NATURALIZACAO,
	   ESTRANGEIRO_CASADO_BRASIL,
	   ESTRANGEIRO_FILHOS_BRASIL,
	   DEMISSAO,
	   ANTERIOR_EMPRESA_CNPJ,
	   ANTERIOR_CNPJ,
	   ANTERIOR_EMPRESA_NOME,
           ANTERIOR_NOME,
	   ANTERIOR_TRABALHADOR_MATRICULA,
           ANTERIOR_MATRICULA,
	   ANTERIOR_TIPO_INSCRICAO,
	   ADMISSAO_TIPO,
	   ADMISSAO_MEIO,
	   ANTERIOR_TRABALHADOR_ADMISSAO,
	   GRAU_INSTRUCAO,
	   BANCO_CONTA_TIPO,
	   CONJUGE
FROM P_TRABALHADORES
left join P_trabalhadores_TRANSF_int
on P_TRABALHADORES.empresa = P_trabalhadores_TRANSF_int.empresa
and P_TRABALHADORES.matricula = P_trabalhadores_TRANSF_int.matricula
ORDER BY 1,2