SELECT P_HORARIOS.*,
P_HORARIOS_INTERVALOS.INTERVALO,
P_HORARIOS_INTERVALOS.INICIO AS INICIO_INTERVALO,
P_HORARIOS_INTERVALOS.TERMINO AS FIM_INTERVALO,
P_HORARIOS_INTERVALOS.TIPO_INTERVALO,
P_HORARIOS_INTERVALOS.DURACAO AS DURACAO_INTERVALO
FROM P_HORARIOS
LEFT JOIN P_HORARIOS_INTERVALOS ON
P_HORARIOS_INTERVALOS.HORARIO = P_HORARIOS.HORARIO AND 
P_HORARIOS_INTERVALOS.TIPO_HORARIO = P_HORARIOS.TIPO_HORARIO
ORDER BY 1,2