SELECT EMPRESA,
C_TIPOS_PLANO_CONTAS.ESTRUTURA
FROM EMPRESAS_EXERCICIOS INNER JOIN C_TIPOS_PLANO_CONTAS
							     ON C_TIPOS_PLANO_CONTAS.TIPO_PLANO_CONTAS = EMPRESAS_EXERCICIOS.TIPO_PLANO_CONTAS 
WHERE EMPRESAS_EXERCICIOS.EXERCICIO = (SELECT FIRST 1 SUB.EXERCICIO
										FROM EMPRESAS_EXERCICIOS AS SUB
									   WHERE SUB.EMPRESA = EMPRESAS_EXERCICIOS.EMPRESA
									   ORDER BY SUB.EXERCICIO DESC)
ORDER BY 1, 2