SELECT F.EMPRESA,
F.MATRICULA,
F.COMPETENCIA,
FR.RUBRICA,
FR.TIPO_RUBRICA,
FR.VALOR,
FR.DADOS
FROM P_TRABALHADORES_FOLHAS F INNER JOIN P_TRABALHADORES_FOLHAS_RUB AS FR
ON F.EMPRESA = FR.EMPRESA
AND F.MATRICULA = FR.MATRICULA
AND F.SEQUENCIAL_FOLHA = FR.SEQUENCIAL_FOLHA
WHERE F.COMPETENCIA > DATEADD(-4 YEAR TO CURRENT_DATE)
  AND EXISTS(SELECT 1 FROM P_SINDICATOS_MEDIAS_RUBRICAS INNER JOIN P_RUBRICAS
						ON P_SINDICATOS_MEDIAS_RUBRICAS.RUBRICA = P_RUBRICAS.RUBRICA
						AND P_SINDICATOS_MEDIAS_RUBRICAS.TIPO_RUBRICA = P_RUBRICAS.TIPO_RUBRICA
					  WHERE P_RUBRICAS.RUBRICA = FR.RUBRICA
					    AND P_RUBRICAS.TIPO_RUBRICA = FR.TIPO_RUBRICA)
ORDER BY 1,3,2