SELECT FO_FUNCIONARIOS.ID_EMPRESA,
       FO_FUNCIONARIOS_AFAST.ID_FUNCIONARIO,
       FO_FUNCIONARIOS_AFAST.AFAINI,
       FO_FUNCIONARIOS_AFAST.AFAFIM,
       FO_FUNCIONARIOS_AFAST.AFAMOT
FROM FO_FUNCIONARIOS_AFAST
INNER JOIN FO_FUNCIONARIOS
	ON FO_FUNCIONARIOS.ID_FUNCIONARIO = FO_FUNCIONARIOS_AFAST.ID_FUNCIONARIO
ORDER BY 1, 2, 3