SELECT FO_ES_RET_LOTE.ID_EMPRESA,
       FO_ES_RET_LOTE_RETORNO.ID_CAMPO,
       FO_ES_RET_LOTE_RETORNO.RECIBO,
       FO_ES_RET_LOTE_RETORNO.CLASSE
FROM FO_ES_RET_LOTE
INNER JOIN FO_ES_RET_LOTE_RETORNO
	ON FO_ES_RET_LOTE_RETORNO.ID_FO_ES_RET_LOTE = FO_ES_RET_LOTE.ID_FO_ES_RET_LOTE
WHERE RECIBO IS NOT NULL
	AND STATUS = 'OK'
	AND CLASSE IN ('S1000', 'S1030', 'S2200', 'S2300')
ORDER BY 1, 4, 2