SELECT FO_HORARIO.ID_EMPRESA,
       FO_HORARIO.ID_FO_HORARIO,
       FO_HORARIO_INTV.INICIO,
       FO_HORARIO_INTV.TERMINO,
       FO_HORARIO_INTV.DURACAO
FROM FO_HORARIO
LEFT JOIN FO_HORARIO_INTV
	ON FO_HORARIO_INTV.ID_FO_HORARIO = FO_HORARIO.ID_FO_HORARIO
WHERE FO_HORARIO_INTV.INICIO IS NOT NULL
ORDER BY 1, 2