SELECT FO_FUNCIONARIOS.ID_EMPRESA,
       FO_F_DEPARTAMENTO.ID_FUNCIONARIO,
       FO_F_DEPARTAMENTO.DATA,
       FO_DEPARTAMENTOS.DEPCOD
FROM FO_F_DEPARTAMENTO
INNER JOIN FO_FUNCIONARIOS
	ON FO_FUNCIONARIOS.ID_FUNCIONARIO = FO_F_DEPARTAMENTO.ID_FUNCIONARIO
INNER JOIN FO_DEPARTAMENTOS
	ON FO_DEPARTAMENTOS.ID_EMPRESA = FO_FUNCIONARIOS.ID_EMPRESA
	AND FO_DEPARTAMENTOS.ID_DEPARTAMENTOS = FO_F_DEPARTAMENTO.ID_DEPARTAMENTO
ORDER BY 1, 2, 3