COALESCE((SELECT MAX(MUNICIPIOG.CD_MUNICIPIO_CONCLA )
            FROM MUNICIPIOG 
           WHERE MUNICIPIOG.CD_MUNICIPIO = AUTONOMO.CD_MUNICIPIO  
             AND MUNICIPIOG.ESTADO = AUTONOMO.ESTADO), (SELECT MAX(MUNICIPIOCGW.CD_MUNICIPIO_IBGE )
												          FROM MUNICIPIOCGW 
												         WHERE MUNICIPIOCGW.CD_MUNICIPIO_IBGE = AUTONOMO.CD_MUNICIPIO  
												           AND MUNICIPIOCGW.UF = AUTONOMO.ESTADO)) AS CODIGO_IBGE_ENDERECO,
COALESCE((SELECT MAX(MUNICIPIOG.CD_MUNICIPIO_CONCLA )
		    FROM MUNICIPIOG 
		   WHERE MUNICIPIOG.CD_MUNICIPIO = AUTONOMO.CD_MUNICIPIO_NASCIMENTO  
	   		 AND MUNICIPIOG.ESTADO = AUTONOMO.ESTADO_NASCIMENTO), (SELECT MAX(MUNICIPIOCGW.CD_MUNICIPIO_IBGE )
																	 FROM MUNICIPIOCGW 
																    WHERE MUNICIPIOCGW.CD_MUNICIPIO_IBGE = AUTONOMO.CD_MUNICIPIO_NASCIMENTO  
																	  AND MUNICIPIOCGW.UF = AUTONOMO.ESTADO_NASCIMENTO)) AS CODIGO_IBGE_NASCIMENTO
	
FROM AUTONOMO
ORDER BY CD_EMPRESA, CD_AUTONOMO