SELECT DISTINCT
BANCO.CD_EMPRESA,
BANCO.CD_BANCO,
BANCO.CODIGO_BANCO, 
FUN.NR_AGENCIA_DEPOSITO AS NR_AGENCIA,
FUN.DV_AGENCIA_DEPOSITO AS DV_AGENCIA,
BANCO.NOME, 
BANCO.CIDADE, 
BANCO.BAIRRO, 
BANCO.ESTADO

FROM BANCO BANCO 
	INNER JOIN FUNFUNCIONAL FUN ON FUN.CD_BANCO_DEPOSITO = BANCO.CD_BANCO
															AND BANCO.CD_EMPRESA = FUN.CD_EMPRESA
ORDER BY 3,4,5