SELECT ENTERPRISE_ID,
CD_FUNCAO,
DESCRICAO,
CBO,
CBO2002,
ATIVIDADES,
ID_ESOCIAL
FROM FUNCAO

UNION ALL

SELECT DISTINCT AUTONOMO.CD_EMPRESA,
      CD_OCUPACAO,      
      DESCRICAO,
      NULL,
	  CD_OCUPACAO,
	  NULL,
	  NULL
  FROM CBOOCUPACAOG,
       AUTONOMO
 WHERE CBOOCUPACAOG.CD_OCUPACAO = AUTONOMO.CBO2002

 ORDER BY 1,2