SELECT 
    fd.ENTERPRISE_ID,
    fd.CD_FUNCIONARIO,
    fd.CD_DEPENDENTE,
    fd.CD_PLANO,
    fd.STATUS,
    MIN(
        STR_TO_DATE(CONCAT(pf.ANO, LPAD(pf.MES, 2, '0'), '01'), '%Y%m%d')
    ) AS DATA_INICIO
FROM 
    CRHPLANOSAUDEFUNDEP fd
INNER JOIN 
    CRHPLANOSAUDEPROCFUNDEP pf
    ON fd.ENTERPRISE_ID = pf.ENTERPRISE_ID
    AND fd.CD_FUNCIONARIO = pf.CD_FUNCIONARIO
    AND fd.CD_DEPENDENTE = pf.CD_DEPENDENTE
    AND fd.CD_PLANO = pf.CD_PLANO
GROUP BY 
    fd.ENTERPRISE_ID,
    fd.CD_FUNCIONARIO,
    fd.CD_DEPENDENTE,
    fd.CD_PLANO,
    fd.STATUS
