SELECT CD_RESPONSAVEL,
       NOME,
       CGC,
       CONTATO,
       DDD_FONE,
       TELEFONE,
       DDD_FAX,
       FAX,
       ENDERECO,
       NR_ENDERECO,
       COMPL_ENDERECO,
       BAIRRO,
       CIDADE,
       ESTADO,
       CEP,
       TIPO_IDENTIFICACAO,
       IDENTIFICACAO,
       CPFCONTATO,
       EMAIL,
       DENOMINACAO,
       RG,
       CRC,
       STATUS,
       UF_CRC,
       CD_MUNICIPIO,
       DT_NASC_RESPONSAVEL,
       CD_CLASSIFICACAO,
       NUMERO_CRP,
       VALIDADE_CRP,
	   (SELECT MUNICIPIOG.CD_MUNICIPIO_CONCLA 
	      FROM MUNICIPIOG 
		 WHERE MUNICIPIOG.CD_MUNICIPIO = RESPONSAVELG.CD_MUNICIPIO
           AND MUNICIPIOG.ESTADO = RESPONSAVELG.ESTADO) AS CODIGO_IBGE
  FROM RESPONSAVELG
ORDER BY 1