SELECT CD_EMPRESA,
       CD_FUNCIONARIO,
       DT_LOTACAO,
       0 AS NR_DIAS,
       CD_TOMADOR
  FROM FUNTOMADOR
UNION ALL
SELECT CD_EMPRESA,
       CD_FUNCIONARIO,
       STR_TO_DATE(CONCAT(ano, '-', LPAD(mes, 2, '0'), '-01'), '%Y-%m-%d') AS DT_LOTACAO,
       NR_DIAS,
       CD_TOMADOR
  FROM RateioDias
 ORDER BY CD_EMPRESA,
          CD_FUNCIONARIO,
          DT_LOTACAO;