SELECT CD_EMPRESA,
       CD_FUNCIONARIO,
       CD_EVENTO,
       CASE
         WHEN LENGTH(MES) = 1 THEN CONVERT(CONCAT(ANO, '0', MES, '01'), DATE)
         WHEN LENGTH(MES) = 2 THEN CONVERT(CONCAT(ANO, MES, '01'), DATE) END AS COMPETENCIA,
       REFERENCIA,
       REFERENCIA_EDITADA,
       VALOR AS VL_BASE
  FROM procevento
 WHERE TIPO = 2
   AND ANO > 2018
   AND MES > 0;