SELECT ENTERPRISE_ID,
CD_FUNCAO,
DESCRICAO,
CBO,
CBO2002,
ATIVIDADES,
ID_ESOCIAL
FROM EBS_CORDILHEIRA.DBO.FUNCAO

UNION ALL

SELECT DISTINCT EBS_CORDILHEIRA.DBO.AUTONOMO.CD_EMPRESA,
      CD_OCUPACAO,      
      DESCRICAO,
      NULL,
	  CD_OCUPACAO,
	  NULL,
	  NULL
  FROM EBS_CORDILHEIRA.DBO.CBOOCUPACAOG,
       EBS_CORDILHEIRA.DBO.AUTONOMO
 WHERE EBS_CORDILHEIRA.DBO.CBOOCUPACAOG.CD_OCUPACAO = EBS_CORDILHEIRA.DBO.AUTONOMO.CBO2002

 ORDER BY 1,2