SELECT  CD_RESPONSAVEL,
		NOME,
		CGC,
		CONTATO,
		DDD_FONE,
		TELEFONE,
		DDD_FAX,
		FAX,
		ENDERECO,
		NR_ENDERECO,
		COMPL_ENDERECO,
		BAIRRO,
		CIDADE,
		ESTADO,
		CEP,
		TIPO_IDENTIFICACAO,
		IDENTIFICACAO,
		CPFCONTATO,
		EMAIL,
		DENOMINACAO,
		RG,
		CRC,
		STATUS,
		UF_CRC,
		CD_MUNICIPIO,
		DT_NASC_RESPONSAVEL,
		CD_CLASSIFICACAO,
		NUMERO_CRP,
		VALIDADE_CRP
  FROM EBS_CORDILHEIRA.DBO.RESPONSAVELG
where EXISTS(SELECT 1 
		       FROM EBS_CORDILHEIRA.DBO.CRDESTABELECIMENTO
			  WHERE CRDESTABELECIMENTO.CD_RESPONSAVEL_ESTABELECIMENTO = RESPONSAVELG.CD_RESPONSAVEL)