SELECT CD_EMPRESA,
       CD_FUNCIONARIO,
       CD_DEPENDENTE,
       NOME,
       DT_NASCIMENTO,
       CPF_DEPENDENTE,
       ESOCIAL_TIPO_PARENTESCO,
       DESCRICAO_PARENTESCO,
	   'E' AS TIPO /* Empregados*/
  FROM EBS_Cordilheira.DBO.FUNDEPENDENTE
  
UNION ALL

SELECT CD_EMPRESA,
       CD_DIRETOR,
       CD_DEPENDENTE,
       NOME,
       DT_NASCIMENTO,
       CPF_DEPENDENTE,
       ESOCIAL_TIPO_PARENTESCO,
       DESCRICAO_PARENTESCO,
	   'C' AS TIPO  /* Contribuintes*/
  FROM EBS_Cordilheira.DBO.CRHDEPENDENTEDIRETOR 
  
UNION ALL

SELECT CD_EMPRESA,
       CD_AUTONOMO,
       CD_DEPENDENTE,
       NOME,
       DT_NASCIMENTO,
       CPF_DEPENDENTE,
       ESOCIAL_TIPO_PARENTESCO,
       DESCRICAO_PARENTESCO,
	   'A' AS TIPO  /* Autonomos*/
  FROM EBS_Cordilheira.DBO.CRHDEPENDENTEAUTONOMO   
  
ORDER BY 1,2,3