 SELECT CD_EMPRESA,
        CD_TABELA AS JORNADA,
        DESCRICAO,
		2 AS TIPO,
		'Segunda' AS DIA,
        TP_DIA_TRABALHO_SEGUNDA AS TIPO_DIA,
        CD_JORNADA_FIXA_SEGUNDA AS HORARIO_DIA,
		OBSERVACOES
   FROM EBS_CORDILHEIRA.DBO.CRHTABELAHORARIO

 union all

