SELECT CRHPLANOSAUDEFUNDEP.ENTERPRISE_ID,
       CRHPLANOSAUDEFUNDEP.CD_FUNCIONARIO,
	   CRHPLANOSAUDEFUNDEP.CD_DEPENDENTE,
	   CRHPLANOSAUDE.CD_OPERADORA,
	   CRHPLANOSAUDEFUNDEP.STATUS,
	   MIN(CASE LEN(CRHPLANOSAUDEPROCFUNDEP.MES)
		WHEN 1 THEN CONVERT(DATE,CONCAT(CRHPLANOSAUDEPROCFUNDEP.ANO, '0', CRHPLANOSAUDEPROCFUNDEP.MES, '01'))
		WHEN 2 THEN CONVERT(DATE,CONCAT(CRHPLANOSAUDEPROCFUNDEP.ANO, CRHPLANOSAUDEPROCFUNDEP.MES, '01')) END) AS DATA_INICIO
FROM EBS_CORDILHEIRA.DBO.CRHPLANOSAUDEFUNDEP INNER JOIN EBS_CORDILHEIRA.DBO.CRHPLANOSAUDEPROCFUNDEP
											      ON CRHPLANOSAUDEFUNDEP.ENTERPRISE_ID = CRHPLANOSAUDEPROCFUNDEP.ENTERPRISE_ID
												 AND CRHPLANOSAUDEFUNDEP.CD_FUNCIONARIO = CRHPLANOSAUDEPROCFUNDEP.CD_FUNCIONARIO
												 AND CRHPLANOSAUDEFUNDEP.CD_DEPENDENTE = CRHPLANOSAUDEPROCFUNDEP.CD_DEPENDENTE
												 AND CRHPLANOSAUDEFUNDEP.CD_PLANO = CRHPLANOSAUDEPROCFUNDEP.CD_PLANO
										  INNER JOIN EBS_CORDILHEIRA.DBO.CRHPLANOSAUDE
										          ON CRHPLANOSAUDE.ENTERPRISE_ID = CRHPLANOSAUDEFUNDEP.ENTERPRISE_ID
												 AND CRHPLANOSAUDE.CD_PLANO = CRHPLANOSAUDEFUNDEP.CD_PLANO
GROUP BY CRHPLANOSAUDEFUNDEP.ENTERPRISE_ID,
       CRHPLANOSAUDEFUNDEP.CD_FUNCIONARIO,
	   CRHPLANOSAUDEFUNDEP.CD_DEPENDENTE,
	   CRHPLANOSAUDE.CD_OPERADORA,
	   CRHPLANOSAUDEFUNDEP.STATUS