SELECT CRHPLANOSAUDEFUN.ENTERPRISE_ID,
       CRHPLANOSAUDEFUN.CD_FUNCIONARIO,
	   CRHPLANOSAUDE.CD_OPERADORA,
	   CRHPLANOSAUDEFUN.STATUS,
	   MIN(CASE LEN(CRHPLANOSAUDEPROCFUN.MES)
		WHEN 1 THEN CONVERT(DATE,CONCAT(CRHPLANOSAUDEPROCFUN.ANO, '0', CRHPLANOSAUDEPROCFUN.MES, '01'))
		WHEN 2 THEN CONVERT(DATE,CONCAT(CRHPLANOSAUDEPROCFUN.ANO, CRHPLANOSAUDEPROCFUN.MES, '01')) END) AS DATA_INICIO
FROM EBS_CORDILHEIRA.DBO.CRHPLANOSAUDEFUN INNER JOIN EBS_CORDILHEIRA.DBO.CRHPLANOSAUDEPROCFUN
											      ON CRHPLANOSAUDEFUN.ENTERPRISE_ID = CRHPLANOSAUDEPROCFUN.ENTERPRISE_ID
												 AND CRHPLANOSAUDEFUN.CD_FUNCIONARIO = CRHPLANOSAUDEPROCFUN.CD_FUNCIONARIO
												 AND CRHPLANOSAUDEFUN.CD_PLANO = CRHPLANOSAUDEPROCFUN.CD_PLANO
										  INNER JOIN EBS_CORDILHEIRA.DBO.CRHPLANOSAUDE
										          ON CRHPLANOSAUDE.ENTERPRISE_ID = CRHPLANOSAUDEFUN.ENTERPRISE_ID
												 AND CRHPLANOSAUDE.CD_PLANO = CRHPLANOSAUDEFUN.CD_PLANO
										        
GROUP BY CRHPLANOSAUDEFUN.ENTERPRISE_ID,
       CRHPLANOSAUDEFUN.CD_FUNCIONARIO,
	   CRHPLANOSAUDE.CD_OPERADORA,
	   CRHPLANOSAUDEFUN.STATUS