SELECT TOMADOR.CD_EMPRESA,
	   TOMADOR.CD_FILIAL,
	   TOMADOR.CD_TOMADOR,
	   TOMADOR.NOME,
	   TOMADOR.ENDERECO,
	   TOMADOR.NR_ENDERECO,
	   TOMADOR.COMPL_ENDERECO,
	   TOMADOR.BAIRRO,
	   TOMADOR.CIDADE,
	   TOMADOR.ESTADO,
	   TOMADOR.CEP,
	   TOMADOR.CGC,
	   TOMADOR.CODIGO_CEI,
	   TOMADOR.CODIGO_FPAS,
	   TOMADOR.CODIGO_TERCEIROS,
	   TOMADOR.ALIQ_SAT,	   
	   TOMADOR.CNAE,
	   TOMADOR.OBRA_CONSTRUCAO_CIVIL,
	   TOMADOR.ESOCIAL_TIPO_TOMADOR AS TIPO_OBRA,
	   TOMADOR.MATRIZ,
	   TOMADOR.CD_PAGAMENTO_INSS_CNPJ,
	   TOMADOR.ESOCIAL_CNO,
	   TOMADOR.ID_ESOCIAL,
	   TOMADOR.ALIQ_FPAS,
	   TOMADOR.ALIQ_FPAS_PROLABORE,
	   TOMADOR.ALIQ_TERCEIROS,
	   COALESCE((SELECT MUNICIPIOG.CD_MUNICIPIO_CONCLA 
	               FROM EBS_CORDILHEIRA.DBO.MUNICIPIOG 
				  WHERE MUNICIPIOG.CD_MUNICIPIO = TOMADOR.CD_MUNICIPIO 
				    AND MUNICIPIOG.ESTADO = TOMADOR.ESTADO), (SELECT MUNICIPIOCGW.CD_MUNICIPIO_IBGE 
					                                            FROM EBS_CORDILHEIRA.DBO.MUNICIPIOCGW  
															   WHERE MUNICIPIOCGW.CD_MUNICIPIO_IBGE = TOMADOR.CD_MUNICIPIO 
															     AND MUNICIPIOCGW.UF = TOMADOR.ESTADO)) AS CODIGO_IBGE	 
FROM EBS_CORDILHEIRA.DBO.TOMADOR
WHERE CD_TOMADOR != 0
ORDER BY CD_EMPRESA, CD_FILIAL, CD_TOMADOR