SELECT CD_EMPRESA,
       CD_FUNCIONARIO,
       DT_LOTACAO,
       0 AS NR_DIAS,
       CD_TOMADOR
  FROM EBS_Cordilheira.DBO.FUNTOMADOR
UNION ALL
SELECT CD_EMPRESA,
       CD_FUNCIONARIO,
       CONVERT(DATETIME, CONCAT(ano, '-', RIGHT(REPLICATE('0', 2) + CAST(mes AS VARCHAR), 2), '-01'), 120) AS DT_LOTACAO,
       NR_DIAS,
       CD_TOMADOR
  FROM EBS_Cordilheira.DBO.RateioDias
 ORDER BY CD_EMPRESA,
          CD_FUNCIONARIO,
          DT_LOTACAO;