SELECT CD_EMPRESA,
        CD_FUNCIONARIO,
        CD_EVENTO,
        CASE LEN(MES)
          WHEN 1 THEN CONVERT(Date,CONCAT(ANO, '0', MES, '01'))
          WHEN 2 THEN CONVERT(Date,CONCAT(ANO, MES, '01')) END AS COMPETENCIA,
        REFERENCIA,
        REFERENCIA_EDITADA,
        VALOR as VL_BASE
   FROM EBS_CORDILHEIRA.DBO.PROCEVENTO
  WHERE TIPO = 2
    AND ANO > 2018
    AND MES > 0;