SELECT 
    FFUNUM_EMPRESA,
	CONCAT(LPAD(FFUSEC_CORR, 2, '0'),LPAD(FFUANO_CORR, 2, '0'),LPAD(FFUMES_CORR, 2, '0')) AS COMPETENCIA,
    FFUTIPO_CARGO,
    FFUCOD_DA_FUNCAO,
    FFUDESCRICAO_DA_FUNCA,
    CONCAT(CAST(FFUCBO_2002_FAMILIA AS CHAR), '', LPAD(CAST(FFUCBO_2002_OCUPACAO AS CHAR), 2, '0')) AS FFUCBO_2002,
    FFUDESC_ATIVIDADE,
    FFUTIPO_FUNCAO
FROM 
    FFUFUNCOESDADOS
ORDER BY 
    FFUNUM_EMPRESA, FFUTIPO_CARGO, COMPETENCIA;