SELECT FCBNUM_EMPRESA,	   
	   CONCAT(LPAD(FCBSEC_CORR, 2, '0'),LPAD(FCBANO_CORR, 2, '0'),LPAD(FCBMES_CORR, 2, '0')) AS COMPETENCIA,
	   FCBTIPO_PROC_CORR,
       FCBCODIGO_EMPREGADO,
	   FCBCONTRATO,
	   FCBMATRICULA_ESOCIAL,
	   RIGHT('0' + CAST(FCBN1 AS CHAR), 2) AS LOCACAO,
	   RIGHT('0' + CAST(FCBN1 AS CHAR), 2) + RIGHT('00' + CAST(FCBN2 AS CHAR), 3)  AS LOCACAO_SERVICO,
	   FCBTIPO,
	   FCBSALARIO,
	   FCBMOTIVO_ALTSAL,
	   FCBADMISSAO,
	   FCBVINCULO_EMPREGATIC,
	   FCBSIST_STATUS,
	   FCBSITU,
	   FCBASSOCIADO_SIND,
	   FCBOPCAO_FGTS,
	   FCBEXPERIENCIA,
	   FCBPRORROGACAO,
	   FCBNUM_DA_CONTA_PAG,
	   FCBDV_CONTA_PAG,
	   FCBCODIGO_DO_TURNO,
	   FCBDEMISSAO_DATA,
	   FCBNOME_EMPREGADO_ANT,
	   FCBCBO,
	   FCBTIPO_CONTA,
	   FCBCODIGO_BANCO_PAG,
	   FCBCODIGO_AG_PAG,
	   FCBCODIGO_DA_FUNCAO,
	   FCBDESCRICAO_FUNCAO,
	   FCBCODIGO_DO_SINDICAT,
	   FCBJORNADA_MENSAL,
	   FCBJORNADA_SEMANAL,
	   FCBCNPJ_EMP_ANTERIOR,
	   FCBADMISSAO_TRANSF,
	   SUBSTRING(FCBID_CC, 1, 14) + '_' + SUBSTRING(FCBID_CC, 16, 9) AS MATRICULA_ESOCIAL_MANUAL
FROM FCBCADCOLDADOSCONTRATO 
ORDER BY 1, 4, 2