SELECT FHTNUM_EMPRESA,
	   CONCAT(LPAD(FHTSEC_CORR, 2, '0'),LPAD(FHTANO_CORR, 2, '0'),LPAD(FHTMES_CORR, 2, '0')) AS COMPETENCIA,
       FHTTIPO_PROC_CORR,
	   FHTCODIGO_DO_TURNO,
	   FHTNOME_DO_TURNO,
	   CAST(FHTNOR_DIA_SEMANA_1 AS CHAR) + CAST(FHTNOR_DIA_SEMANA_2 AS CHAR) + CAST(FHTNOR_DIA_SEMANA_3 AS CHAR) + CAST(FHTNOR_DIA_SEMANA_4 AS CHAR) + CAST(FHTNOR_DIA_SEMANA_5 AS CHAR) + CAST(FHTNOR_DIA_SEMANA_6 AS CHAR) + CAST(FHTNOR_DIA_SEMANA_7 AS CHAR) AS SEQUENCIA_DIAS,
	   FHTNOR_DIA_SEMANA_1,
	   FHTNOR_DIA_SEMANA_2,
	   FHTNOR_DIA_SEMANA_3,
	   FHTNOR_DIA_SEMANA_4,
	   FHTNOR_DIA_SEMANA_5,
	   FHTNOR_DIA_SEMANA_6,
	   FHTNOR_DIA_SEMANA_7,
	   FHTNOR_HRS_DIA,
	   FHTDIF_DIA_SEMANA_1,
	   FHTDIF_DIA_SEMANA_2,
	   FHTDIF_HRS_DIA,
	   FHTREP_DIA_SEMANA_1,
	   FHTREP_DIA_SEMANA_2,
	   FHTHORA_ENTRADA,
	   FHTHORA_SAIDA,
	   FHTINTERVALO_ENTRADA,
	   FHTINTERVALO_SAIDA,
	   FHTDIF_ENTRADA,
	   FHTDIF_SAIDA,
	   FHTDIF_INT_ENTRADA,
	   FHTDIF_INT_SAIDA
FROM FHTTURNOSDADOS 
ORDER BY 1, 4, 2, 3
