SELECT FNVNUM_EMPRESA,
	   CONCAT(LPAD(FNVSEC_CORR, 2, '0'),LPAD(FNVANO_CORR, 2, '0'),LPAD(FNVMES_CORR, 2, '0')) AS COMPETENCIA,
	   FNVTIPO_PROC_CORR,
	   FNVID,
	   FNVN1,
	   FNVN2,
	   FNVN3,
	   FNVN4,
	   FNVNOME_NIVEL,
	   FNVENDERECO,
	   FNVNUMERO,
	   FNVBAIRRO,
	   FNVCEP,
	   FNVMUNICIPIO,
	   FNVUF,
	   FNVTELEFONE_DDD_NIV,
	   FNVTELEFONE_NUM_NIV,
	   FNVTOMADOR_SERV_SN,
	   '' AS TIPO_INSCRICAO,
	   FNVCGC,
	   FNVCOD_ATIV_ECON,
	   FNVCOMPLEMENTO,
	   FNVTOMADOR_SERV_SN AS FILIAL,
	   FNVNOME_NIVEL AS RAZAO,
	   FNVCOD_FPAS,
	   FNVCOD_TERCEIROS,
	   '' AS INSS_EMPRESA,
	   '' AS ACID_TRABALHO,
	   FNVLOCACAO_ATIVA,
	   FNVCEI
FROM FNVLOCACOESDADOS
ORDER BY 1, 4, 2
