SELECT FARNUM_EMPRESA,
	   CONCAT(LPAD(FARSEC_CORR, 2, '0'),LPAD(FARANO_CORR, 2, '0'),LPAD(FARMES_CORR, 2, '0')) AS COMPETENCIA,
	   FARTIPO_PROC_CORR,
	   FARARQ_FUN,
	   FARARQ_BAN,
	   FARARQ_EVP,
	   FARARQ_SIN,
	   FARARQ_HOR,
	   FAREMP_FUN,
	   FAREMP_BAN,
	   FAREMP_EVP,
	   FAREMP_SIN,
	   FAREMP_HOR
FROM FARPARAMCOMPARTILHAMENTODETABELAS
ORDER BY 1, 2
