SELECT FFUNUM_EMPRESA,
	   DBO.GET_COMPETENCE(FFUSEC_CORR, FFUANO_CORR, FFUMES_CORR) AS COMPETENCIA,
	   FFUTIPO_CARGO,
       FFUCOD_DA_FUNCAO,
	   FFUDESCRICAO_DA_FUNCA,
	   CAST(FFUCBO_2002_FAMILIA AS VARCHAR) + '' + RIGHT('0' + CAST(FFUCBO_2002_OCUPACAO AS VARCHAR), 2) AS FFUCBO_2002,
	   FFUDESC_ATIVIDADE,
	   FFUTIPO_FUNCAO
FROM FFUFUNCOESDADOS
ORDER BY 1, 3, 2