SELECT FDENUM_EMPRESA,
       DBO.GET_COMPETENCE(FDESEC_CORR, FDEANO_CORR, FDEMES_CORR) AS COMPETENCIA,
	   FDETIPO_DO_DEPENDENTE,
	   FDECODIGO_EMPREGADO,
	   FDESEQUENCIA,
	   FDECPF_DEPENDENTE,
	   FDETIPO_DO_DEPENDENTE AS PARENTESCO,
	   FDEDEP_IR,
	   FDEDEP_SF,
	   FDENOME_DO_DEPENDENTE,
	   FDEDATA_NASCTO,
	   FDELOCAL_NASCIMENTO,
	   FDECARTORIO,
	   FDENR_REGISTRO,
	   FDENR_LIVRO,
	   FDENR_FOLHA,
	   FDEDATA_ENTR_CERTIDAO,
	   FDEDT_NASC_BENEF,
	   '' AS MATRICULA_CERTIDAO_NASCIMENTO
FROM FDECADCOLDEPENDENTES
ORDER BY 1, 4, 2, 5
