SELECT AEMCODIGO_EMPRESA,
	   AEMEXERCICIO,
	   AEMDESC_LOGR,
	   AEMENDERECO,
	   AEMNUMERO,
	   AEMCOMPLEMENTO,
	   AEMBAIRRO,
	   AEMMUNICIPIO,
	   AEMCEP,
	   AEMDDD_FONE,
	   AEMNUM_FONE,
	   AEMNAT_JURIDICA,
	   AEMCODIGO_ATIVIDADE,
	   AEMNUM_REGISTRO,
	   AEMDATA_REGISTRO,
	   AEMDTINSCR_CGC,
	   AEMDTINSCR_CAD,
	   '' AS TIPO_INSCRICAO,
	   RIGHT('000' + CAST(AEMINSC_FEDERAL_BD AS VARCHAR), 14) AS AEMINSC_FEDERAL_BD,
	   AEMEMAIL_EMPR,
	   AEMRAZAO_SOCIAL_BD,
	   AEMINSC_EST_BD,
	   AEMUF_BD,
	   AEMCOD_AUDITOR
FROM AEMEMPRESAS 
ORDER BY 1, 2
