IF NOT EXISTS (SELECT 1 FROM sys.objects WHERE  name = 'GET_COMPETENCE' AND type = 'FN')
BEGIN
	EXEC('CREATE FUNCTION DBO.GET_COMPETENCE(@ANO_PARTE_01 INTEGER, @ANO_PARTE_02 INTEGER, @MES INTEGER)
		  RETURNS VARCHAR(6)
	      BEGIN
			RETURN(RIGHT(''0'' + CAST(@ANO_PARTE_01 AS VARCHAR), 2) + '''' + RIGHT(''0'' + CAST(@ANO_PARTE_02 AS VARCHAR), 2) + '''' + RIGHT(''0'' + CAST(@MES AS VARCHAR), 2))
	      END')
END
