SELECT EMP.FEMNUM_EMPRESA,
       DBO.GET_COMPETENCE(EMP.FEMSEC_CORR, EMP.FEMANO_CORR, EMP.FEMMES_CORR) AS COMPETENCIA,
       EMP.FEMCATEGORIA_EMPREG,
	   '' AS DESCONHECIDO,
	   EMP.FEMCODIGO_RESP,
	   SIND.FGISIND_PATRONAL,
	   EMP.FEMDIRF_ENTREGA_RESP
FROM FEMEMPRESADADOS EMP
LEFT JOIN FGICONTSINDPARAMETROS SIND
ON EMP.FEMNUM_EMPRESA = SIND.FGINUM_EMPRESA
ORDER BY 1, 2
