SELECT FARNUM_EMPRESA,
       DBO.GET_COMPETENCE(FARSEC_CORR, FARANO_CORR, FARMES_CORR) AS COMPETENCIA,
	   FARTIPO_PROC_CORR,
	   FARARQ_FUN,
	   FARARQ_BAN,
	   FARARQ_EVP,
	   FARARQ_SIN,
	   FARARQ_HOR,
	   FAREMP_FUN,
	   FAREMP_BAN,
	   FAREMP_EVP,
	   FAREMP_SIN,
	   FAREMP_HOR
FROM FARPARAMCOMPARTILHAMENTODETABELAS
ORDER BY 1, 2
