SELECT '{empresa}' AS EMPRESA,
	   CODDEPTO,
	   LOCAL,
	   DEPSETSEC,
	   SETOR,
	   SECAO,
	   CGC,
	   ENDER,
	   NUMEND,
	   CIDADE,
	   CEP,
	   UF,
	   CODGPS,
	   PERCTERC,
	   PERCSAT,
	   PERCINSS,
	   FPAS,
	   CODTERC,
	   BAIRRODEP,
	   CODMUNIC,
	   IE,
	   EMAIL,
	   CONTATO,
	   ID_ESOCIAL,
	   TPLOTACAO,
       INICIO	   
FROM f{empresa}.depto
where TPLOTACAO <> '01' or DEPSETSEC = '000100000000'
order by 1,2