SELECT CFE.EMP_CODIGO,
	   CON.CODIGO,
	   CON.NATUREZA,
	   CFE.CODIGO as GRUPO
FROM CFE INNER JOIN CON
	             ON CON.EMP_CODIGO = CFE.EMP_CODIGO
	             AND CON.CODIGO = CFE.VALOR
WHERE CFE.CODIGO IN ('ATIVO', 'PASSIVO', 'PATRIMONIOLIQUIDO', 'DESPESAS', 'RECEITAS', 'LUCROSPREJUIZOS')
ORDER BY 1, 2