select EMP_CODIGO,
	   EPG_CODIGO,
	   TIPOEXAME,
	   DATA,
	   RESULTADO,
	   VENCIMENTO,
	   PRS_CODIGO_MED
from ASO
UNION
select EMP_CODIGO,
	   CODIGO,
	   2,
	   EXAMEMEDICODTULTIMO,
	   1,
	   dateadd (month, EXAMEMEDICOVALIDADE, EXAMEMEDICODTULTIMO),
	   0
from EPG
WHERE NOT EXAMEMEDICODTULTIMO IS NULL
order by 1, 3, 4