SELECT ID,
       EMP_CODIGO AS CODI_EMP,
       '' AS CODIGO,
       RECIBO AS NUMERO_RECIBO,
       '' AS CODIGO_ESOCIAL,
       RIGHT (CODIGO_EVENTO,4) AS TIPO_EVENTO,
       CHAVECOMPLEMENTAR,
       CHAVECOMPLEMENTAR2,
       ACAO
FROM ES_EVENTO
WHERE RECIBO <> ''
	AND CODIGO_EVENTO = 'S-1000'

UNION

SELECT ID,
       EMP_CODIGO AS CODI_EMP,
       CHAVE AS CODIGO,
       RECIBO AS NUMERO_RECIBO,
       CHAVE AS CODIGO_ESOCIAL,
       RIGHT (CODIGO_EVENTO,4) AS TIPO_EVENTO,
       CHAVECOMPLEMENTAR,
       CHAVECOMPLEMENTAR2,
       ACAO
FROM ES_EVENTO
WHERE RECIBO <> ''
	AND CODIGO_EVENTO = 'S-1005'

UNION

SELECT ES_EVENTO.ID,
       ES_EVENTO.EMP_CODIGO AS CODI_EMP,
       SLTR.LTR_CODIGO AS CODIGO,
       ES_EVENTO.RECIBO AS NUMERO_RECIBO,
       SLTR.LTR_CODIGO AS CODIGO_ESOCIAL,
       RIGHT(ES_EVENTO.CODIGO_EVENTO, 4) AS TIPO_EVENTO,
       ES_EVENTO.CHAVECOMPLEMENTAR,
       ES_EVENTO.CHAVECOMPLEMENTAR2,
       ES_EVENTO.ACAO
FROM ES_EVENTO
INNER JOIN SLTR
  ON ES_EVENTO.EMP_CODIGO = SLTR.EMP_CODIGO
  AND ES_EVENTO.CHAVE = SLTR.LTR_CODIGO
  AND  ES_EVENTO.CHAVECOMPLEMENTAR = SLTR."DATA"
WHERE RECIBO <> ''
  AND CODIGO_EVENTO = 'S-1020'

UNION

SELECT ES_EVENTO.ID,
       ES_EVENTO.EMP_CODIGO AS CODI_EMP,
       CAR.CODIGO AS CODIGO,
       RECIBO AS NUMERO_RECIBO,
       REPLACE(CAR.CODIGO, ',', '') AS CODIGO_ESOCIAL,
       RIGHT (CODIGO_EVENTO, 4) AS TIPO_EVENTO,
       CHAVECOMPLEMENTAR,
       CHAVECOMPLEMENTAR2,
       ES_EVENTO.ACAO
FROM ES_EVENTO
LEFT JOIN CAR
	ON CAR.CODIGO = ES_EVENTO.CHAVE
	AND CAR.EMP_CODIGO = ES_EVENTO.EMP_CODIGO
WHERE RECIBO <> ''
	AND CODIGO_EVENTO = 'S-1030'

UNION

SELECT ID,
       EMP_CODIGO AS CODI_EMP,
       CHAVE AS CODIGO,
       RECIBO AS NUMERO_RECIBO,
       REPLACE(CHAVE, ',', '') AS CODIGO_ESOCIAL,
       RIGHT (CODIGO_EVENTO, 4) AS TIPO_EVENTO,
       CHAVECOMPLEMENTAR,
       CHAVECOMPLEMENTAR2,
       ACAO
FROM ES_EVENTO
WHERE RECIBO <> ''
	AND CODIGO_EVENTO = 'S-1050'

UNION

SELECT ES_EVENTO.ID,
       ES_EVENTO.EMP_CODIGO AS CODI_EMP,
       EPG.CODIGO AS CODIGO,
       RECIBO AS NUMERO_RECIBO,
       REPLACE(EPG.MATRICULAESOCIAL, ',', '') AS CODIGO_ESOCIAL,
       RIGHT (ES_EVENTO.CODIGO_EVENTO, 4) AS TIPO_EVENTO,
       CHAVECOMPLEMENTAR,
       CHAVECOMPLEMENTAR2,
       ES_EVENTO.ACAO
FROM ES_EVENTO
LEFT JOIN EPG
	ON EPG.CODIGO = ES_EVENTO.CHAVE
	AND EPG.EMP_CODIGO = ES_EVENTO.EMP_CODIGO
WHERE RECIBO <> ''
	AND ES_EVENTO.CODIGO_EVENTO IN ('S-2200','S-2300')
	AND CODIGO <> ''

UNION

SELECT ES_EVENTO.ID,
       ES_EVENTO.EMP_CODIGO AS CODI_EMP,
       EPG.CODIGO AS CODIGO,
       RECIBO AS NUMERO_RECIBO,
       REPLACE(EPG.MATRICULAESOCIAL, ',', '') AS CODIGO_ESOCIAL,
       RIGHT (ES_EVENTO.CODIGO_EVENTO, 4) AS TIPO_EVENTO,
       CHAVECOMPLEMENTAR,
       CHAVECOMPLEMENTAR2,
       ES_EVENTO.ACAO
FROM ES_EVENTO
LEFT JOIN EPG
	ON EPG.CODIGO = ES_EVENTO.CHAVE
	AND EPG.EMP_CODIGO = ES_EVENTO.EMP_CODIGO
WHERE RECIBO <> ''
	AND ES_EVENTO.CODIGO_EVENTO IN ('S-2205','S-2206')
	AND CODIGO <> ''

UNION

SELECT ES_EVENTO.ID,
       ES_EVENTO.EMP_CODIGO AS CODI_EMP,
       EPG.CODIGO AS CODIGO,
       RECIBO AS NUMERO_RECIBO,
       REPLACE(EPG.MATRICULAESOCIAL, ',', '') AS CODIGO_ESOCIAL,
       RIGHT (ES_EVENTO.CODIGO_EVENTO, 4) AS TIPO_EVENTO,
       CHAVECOMPLEMENTAR,
       CHAVECOMPLEMENTAR2,
       ES_EVENTO.ACAO
FROM ES_EVENTO
LEFT JOIN EPG
	ON EPG.CODIGO = ES_EVENTO.CHAVE
	AND EPG.EMP_CODIGO = ES_EVENTO.EMP_CODIGO
WHERE RECIBO <> ''
	AND ES_EVENTO.CODIGO_EVENTO IN ('S-2230')
	AND CODIGO <> ''

ORDER BY 2,6,1;