SELECT EMP_CODIGO,
ID,
JOR_ID,
DATA,
DATAFINAL,
HORAENTRADA,
HORASAIDA,
FLEXIVEL,
DATAANT,
DATAFINALANT,
ACAO,
STATUS,
EXCLUISOMENTEESOCIAL,
(select JOR.NOME from JOR where JOR.EMP_Codigo = SJOR.EMP_Codigo and JOR.ID = SJOR.JOR_ID) as NOME,
(select JOR.CODIGO from JOR where JOR.EMP_Codigo = SJOR.EMP_Codigo and JOR.ID = SJOR.JOR_ID) as CODIGO_HOR,
(select JOR.CODIGO_ESOCIAL from JOR where JOR.EMP_Codigo = SJOR.EMP_Codigo and JOR.ID = SJOR.JOR_ID) as CODIGO_ESOCIAL
FROM SJOR
WHERE DATAFINAL IS NULL
ORDER BY 1, 3, 4 DESC