SELECT LIC.EMP_CODIGO,
       LIC.EPG_CODIGO,
       LIC.DTINICIAL,
       LIC.DTFINAL,
       LIC.TLI_CODIGO,
       TLI.NOME,
       TLI.TB_TLI_CODIGO,
       datediff(day, LIC.DTINICIAL , LIC.DTFINAL) + 1 AS DIAS,
       '' AS TIPO_ADESAO,
       '' AS PERC_REDUCAO
  FROM LIC
 INNER JOIN TLI
    ON TLI.CODIGO = LIC.TLI_CODIGO
UNION
SELECT ABE.EMP_CODIGO,
       EBE.EPG_CODIGO,
       ABE."DATA" AS DTINICIAL,
       ABE.DATA_FINAL AS DTFINAL,
       'COVID' AS TLI_CODIGO,
       ABE.DESCRICAO AS NOME,
       '' AS TB_TLI_CODIGO,
       ABE.DIAS_DURACAO AS DIAS,
       ABE.TIPO_ADESAO,
       ABE.PERC_REDUCAO
  FROM ABE
 INNER JOIN EBE
    ON ABE.EMP_CODIGO = EBE.EMP_CODIGO
   AND ABE.CODIGO = EBE.ABE_CODIGO
 ORDER BY 1, 2, 3
