SELECT PAE.*,
(select max(PAF.FALTAS) FROM PAF WHERE PAF.PAE_EMP_CODIGO = PAE.EMP_CODIGO  AND PAF.PAE_EPG_CODIGO = PAE.EPG_CODIGO AND PAF.PAE_DTINICIAL = PAE.DTINICIAL) AS DIAS_FALTA,
(SELECT min(FER.DTGOZOINICIAL) FROM FER, PAF 
	WHERE FER.EMP_CODIGO = PAE.EMP_CODIGO
	AND pae.EPG_CODIGO = FER.EFO_EPG_CODIGO
	AND PAF.PAE_DTINICIAL = PAE.DTINICIAL
	AND PAF.FER_EMP_CODIGO = FER.EMP_CODIGO  
	AND PAF.FER_EFO_EPG_CODIGO = FER.EFO_EPG_CODIGO 
	AND PAF.FER_EFO_FOL_SEQ = FER.EFO_FOL_SEQ) AS DATA_INI_GOZO,
(SELECT min(FER.DTGOZOINICIAL) FROM FER, PAF 
	WHERE FER.EMP_CODIGO = PAE.EMP_CODIGO
	AND pae.EPG_CODIGO = FER.EFO_EPG_CODIGO
	AND PAE.DTINICIAL < PAF.PAE_DTINICIAL
	AND PAF.FER_EMP_CODIGO = FER.EMP_CODIGO  
	AND PAF.FER_EFO_EPG_CODIGO = FER.EFO_EPG_CODIGO 
	AND PAF.FER_EFO_FOL_SEQ = FER.EFO_FOL_SEQ) AS DATA_INI_GOZO_POSTERIOR,
(SELECT SUM(datediff(day , LIC.DTINICIAL , LIC.DTFINAL)+1)
FROM LIC
WHERE LIC.TLI_CODIGO NOT IN (1,16)
AND PAE.EMP_CODIGO = LIC.EMP_CODIGO 
AND PAE.EPG_CODIGO = LIC.EPG_CODIGO
AND (PAE.DTINICIAL BETWEEN LIC.DTINICIAL AND LIC.DTFINAL OR PAE.DTFINAL BETWEEN LIC.DTINICIAL AND LIC.DTFINAL)) AS DIAS_AFASTADO,
(SELECT min(LIC.DTINICIAL)
FROM LIC
WHERE LIC.TLI_CODIGO NOT IN (1,16)
AND PAE.EMP_CODIGO = LIC.EMP_CODIGO 
AND PAE.EPG_CODIGO = LIC.EPG_CODIGO
AND (PAE.DTINICIAL BETWEEN LIC.DTINICIAL AND LIC.DTFINAL OR PAE.DTFINAL BETWEEN LIC.DTINICIAL AND LIC.DTFINAL)) AS DATA_INI_AFASTAMENTO,
(SELECT max(LIC.DTFINAL)
FROM LIC
WHERE LIC.TLI_CODIGO NOT IN (1,16)
AND PAE.EMP_CODIGO = LIC.EMP_CODIGO 
AND PAE.EPG_CODIGO = LIC.EPG_CODIGO
AND (PAE.DTINICIAL BETWEEN LIC.DTINICIAL AND LIC.DTFINAL OR PAE.DTFINAL BETWEEN LIC.DTINICIAL AND LIC.DTFINAL)) AS DATA_FIM_AFASTAMENTO
FROM PAE
ORDER BY 1, 2, 3